package com.syntax.class09;

public class ArrayPrintSumOf2Integers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] b=new int[4];
		//store values
		b[0]=90;
		b[1]=85;
		b[2]=70;
		b[3]=100;
		
	 
		System.out.println(b[2]); //70
		System.out.println(b[1]+b[3]); //185
		
		
		
		
		
		
	}

}
